mapper.update:
update event
set store_filename=#{storeFilename}, upload_filename=#{uploadFilename}, path=#{path}, title=#{title}, content=#{content}, start_date=#{startDate}, end_date=#{endDate},
    thum_store_filename=#{thumStoreFilename}, thum_upload_filename=#{thumUploadFilename}, thum_path=#{thumPath}
where id=#{id}

mapper.select:
select * from event where title like concat('%', #{searchWord}, '%')
and start_date <= now() and now() <= end_date
and end_date < now()
and start_date > now()
/*인기순*/ order by view_count desc
/*최신순*/ order by reporting_date desc

mapper.update:
update event
set view_count = view_count + 1
where id = #{id}
