mapper.select:
select m.*, d.name as directorName, g.name as genreName, d.id as directorId, g.id as genreId from movie m left join director d on m.director_id = d.id left join genre g on m.genre_id = g.id where m.screening_format like concat('%',#{format},'%')
and m.genre_id in
#{opt}
and m.release_date <= now()
and m.release_date > now()
order by m.release_date limit #{startPage}, #{perPageNum}

mapper.update:
update movie
set poster=#{poster}, title=#{title}, summary=#{summary}, language=#{language}, show_times=#{showTimes},
    release_date=#{releaseDate}, rating=#{rating}, screening_format=#{joinFormat},
    director_id=#{directorId}, genre_id=#{genreId}
where id=#{id}
