mapper.select:
select seat.*, theater.name as theaterName, cinema.name as cinemaName from seat
left join schedule on schedule.id = seat.schedule_id left join theater on schedule.theater_id = theater.id left join cinema on cinema.id = theater.cinema_id
 where schedule.theater_id = #{id}

select seat.*, theater.name as theaterName, theater.id as theater_id, cinema.name as cinemaName from seat
left join schedule on schedule.id = seat.schedule_id left join theater on schedule.theater_id = theater.id left join cinema on cinema.id = theater.cinema_id
 where seat.id = #{id}

mapper.update:
update seat
set number=#{number}, schedule_id=#{schedule_id}, reservation_id=#{reservation_id}
where id=#{id}
